import React, { useState } from 'react';
import api from '../../services/api';

const ZapScanner: React.FC = () => {
  const [targetUrl, setTargetUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);

  const startScan = async () => {
    if (!targetUrl) return;
    setLoading(true);
    setError(null);
    setResult(null);
    try {
      const res = await api.post('/zap/scan', { targetUrl });
      setResult(res.data);
    } catch (e: any) {
      setError(e?.response?.data?.error || 'Failed to start scan');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 style={{ marginTop: 0 }}>OWASP ZAP</h2>
      <p style={{ color: '#9ca3af' }}>Enter a target URL to start a ZAP scan (demo endpoint).</p>
      <div style={{ display: 'flex', gap: 8, marginTop: 8 }}>
        <input
          value={targetUrl}
          onChange={(e) => setTargetUrl(e.target.value)}
          placeholder="https://example.com"
          style={{ flex: 1, padding: 8, borderRadius: 6, border: '1px solid #1f2937', background: '#0b1220', color: '#e5e7eb' }}
        />
        <button onClick={startScan} disabled={loading || !targetUrl} style={{ padding: '8px 12px', borderRadius: 6 }}>
          {loading ? 'Starting...' : 'Start Scan'}
        </button>
      </div>
      {error && <div style={{ color: '#f87171', marginTop: 12 }}>{error}</div>}
      {result && (
        <pre style={{ marginTop: 12, background: '#0b1220', padding: 12, borderRadius: 8, border: '1px solid #1f2937', overflowX: 'auto' }}>
{JSON.stringify(result, null, 2)}
        </pre>
      )}
    </div>
  );
};

export default ZapScanner;
