import React, { useState } from 'react';
import api from '../../services/api';

const presets = [
  { label: 'Quick (-T4 -F)', value: '-T4 -F' },
  { label: 'Service Detect (-sV)', value: '-sV' },
  { label: 'Aggressive (-T4 -A -v)', value: '-T4 -A -v' },
  { label: 'Vuln Scripts (--script=vuln)', value: '--script=vuln' },
];

const NmapScanner: React.FC = () => {
  const [target, setTarget] = useState('');
  const [options, setOptions] = useState('-sV');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState<string>('');
  const [error, setError] = useState<string | null>(null);

  const startScan = async () => {
    if (!target) return;
    setLoading(true);
    setError(null);
    setResult('');
    try {
      const res = await api.post('/nmap/scan', { target, options });
      setResult(res.data?.results || '');
    } catch (e: any) {
      setError(e?.response?.data?.error || 'Scan failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 style={{ marginTop: 0 }}>Nmap</h2>
      <p className="ll-muted">Scan targets for open ports and services.</p>

      <div className="ll-card ll-card--purple" style={{ marginTop: 12 }}>
        <div className="ll-grid" style={{ maxWidth: 860 }}>
          <input
            className="ll-input"
            value={target}
            onChange={(e) => setTarget(e.target.value)}
            placeholder="Target (e.g., 192.168.1.1 or example.com)"
          />

          <div className="ll-row" style={{ flexWrap: 'wrap' }}>
            {presets.map(p => (
              <button
                className="ll-button"
                key={p.value}
                onClick={() => setOptions(p.value)}
                style={{ filter: options === p.value ? 'brightness(1.05)' : undefined }}
              >
                {p.label}
              </button>
            ))}
          </div>

          <div className="ll-row">
            <input
              className="ll-input"
              value={options}
              onChange={(e) => setOptions(e.target.value)}
              placeholder="Custom options"
              style={{ flex: 1 }}
            />
            <button className="ll-button" onClick={startScan} disabled={loading || !target}>
              {loading ? 'Scanning...' : 'Start Scan'}
            </button>
          </div>
        </div>
        {error && <div style={{ color: '#f87171', marginTop: 10 }}>{error}</div>}
      </div>

      {result && (
        <div className="ll-card ll-card--purple" style={{ marginTop: 12 }}>
          <pre>{result}</pre>
        </div>
      )}
    </div>
  );
};

export default NmapScanner;
