import React, { useEffect, useRef, useState } from 'react';
import api from '../../services/api.ts';

type AlertsResponse = { alerts: any[] };

const ZapScanner: React.FC = () => {
  const [targetUrl, setTargetUrl] = useState('');
  const [spiderId, setSpiderId] = useState<string | null>(null);
  const [scanId, setScanId] = useState<string | null>(null);
  const [spiderProgress, setSpiderProgress] = useState<number>(0);
  const [scanProgress, setScanProgress] = useState<number>(0);
  const [alerts, setAlerts] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const spiderTimer = useRef<number | null>(null);
  const scanTimer = useRef<number | null>(null);

  useEffect(() => {
    return () => {
      if (spiderTimer.current) window.clearInterval(spiderTimer.current);
      if (scanTimer.current) window.clearInterval(scanTimer.current);
    };
  }, []);

  const startSpider = async () => {
    if (!targetUrl) return;
    setError(null);
    setAlerts([]);
    setSpiderProgress(0);
    setScanProgress(0);
    setLoading(true);
    try {
      const { data } = await api.post('/zap/spider', { targetUrl });
      setSpiderId(data.scanId);
      // Poll status
      spiderTimer.current = window.setInterval(async () => {
        const { data: s } = await api.get(`/zap/spider/${data.scanId}`);
        setSpiderProgress(s.status);
        if (s.status >= 100) {
          if (spiderTimer.current) window.clearInterval(spiderTimer.current);
          setLoading(false);
        }
      }, 1200);
    } catch (e: any) {
      setError(e?.response?.data?.error || 'Failed to start spider');
      setLoading(false);
    }
  };

  const startActiveScan = async () => {
    if (!targetUrl) return;
    setError(null);
    setScanProgress(0);
    setLoading(true);
    try {
      const { data } = await api.post('/zap/activescan', { targetUrl });
      setScanId(data.scanId);
      scanTimer.current = window.setInterval(async () => {
        const { data: s } = await api.get(`/zap/activescan/${data.scanId}`);
        setScanProgress(s.status);
        if (s.status >= 100) {
          if (scanTimer.current) window.clearInterval(scanTimer.current);
          setLoading(false);
          // fetch alerts
          const { data: a } = await api.get<AlertsResponse>(`/zap/alerts`, { params: { baseUrl: targetUrl } });
          setAlerts(a.alerts || []);
        }
      }, 1500);
    } catch (e: any) {
      setError(e?.response?.data?.error || 'Failed to start active scan');
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 style={{ marginTop: 0 }}>OWASP ZAP</h2>
      <p style={{ color: '#9ca3af' }}>Run spider and active scan, then view alerts.</p>
      <div style={{ display: 'flex', gap: 8, marginTop: 8 }}>
        <input
          value={targetUrl}
          onChange={(e) => setTargetUrl(e.target.value)}
          placeholder="https://example.com"
          style={{ flex: 1, padding: 8, borderRadius: 6, border: '1px solid #1f2937', background: '#0b1220', color: '#e5e7eb' }}
        />
        <button onClick={startSpider} disabled={loading || !targetUrl} style={{ padding: '8px 12px', borderRadius: 6 }}>
          {loading ? 'Working...' : 'Start Spider'}
        </button>
        <button onClick={startActiveScan} disabled={loading || !targetUrl} style={{ padding: '8px 12px', borderRadius: 6 }}>
          {loading ? 'Working...' : 'Start Active Scan'}
        </button>
      </div>
      {error && <div style={{ color: '#f87171', marginTop: 12 }}>{error}</div>}

      {(spiderId || scanId) && (
        <div style={{ marginTop: 12, display: 'grid', gap: 8 }}>
          {spiderId && (
            <div style={{ background: '#111827', padding: 12, borderRadius: 8, border: '1px solid #1f2937' }}>
              <strong>Spider Progress:</strong> {spiderProgress}%
              <div style={{ height: 8, background: '#0b1220', borderRadius: 6, marginTop: 6 }}>
                <div style={{ height: '100%', width: `${spiderProgress}%`, background: '#2563eb', borderRadius: 6 }} />
              </div>
            </div>
          )}
          {scanId && (
            <div style={{ background: '#111827', padding: 12, borderRadius: 8, border: '1px solid #1f2937' }}>
              <strong>Active Scan Progress:</strong> {scanProgress}%
              <div style={{ height: 8, background: '#0b1220', borderRadius: 6, marginTop: 6 }}>
                <div style={{ height: '100%', width: `${scanProgress}%`, background: '#10b981', borderRadius: 6 }} />
              </div>
            </div>
          )}
        </div>
      )}

      {alerts.length > 0 && (
        <div style={{ marginTop: 16 }}>
          <h3 style={{ margin: '8px 0' }}>Alerts ({alerts.length})</h3>
          <div style={{ display: 'grid', gap: 8 }}>
            {alerts.map((a: any, i: number) => (
              <div key={i} style={{ background: '#111827', padding: 12, borderRadius: 8, border: '1px solid #1f2937' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <strong>{a.alert}</strong>
                  <span style={{ color: '#93c5fd' }}>{a.risk}</span>
                </div>
                <div style={{ color: '#9ca3af', marginTop: 4 }}>{a.description}</div>
                {a.instances && a.instances.length > 0 && (
                  <div style={{ marginTop: 6, fontSize: 12, color: '#9ca3af' }}>
                    {a.instances[0].uri}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ZapScanner;
