import React, { useState, useRef, ChangeEvent } from 'react';
import { saveAs } from 'file-saver';
import { Buffer } from 'buffer';
import CryptoJS from 'crypto-js';

type Algorithm = 'AES-128-CBC' | 'AES-192-CBC' | 'AES-256-CBC' | 'DES-CBC' | '3DES-CBC';

interface LogEntry {
  timestamp: string;
  message: string;
  type: 'info' | 'success' | 'error' | 'warning';
}

const FileEncryptor: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [password, setPassword] = useState('');
  const [algorithm, setAlgorithm] = useState<Algorithm>('AES-256-CBC');
  const [isProcessing, setIsProcessing] = useState(false);
  const [logs, setLogs] = useState<LogEntry[]>([]);
  const [operation, setOperation] = useState<'encrypt' | 'decrypt'>('encrypt');
  const fileInputRef = useRef<HTMLInputElement>(null);

  const addLog = (message: string, type: LogEntry['type'] = 'info') => {
    const timestamp = new Date().toLocaleTimeString();
    setLogs(prev => [...prev, { timestamp, message, type }]);
  };

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setSelectedFile(e.target.files[0]);
      addLog(`Selected file: ${e.target.files[0].name}`, 'info');
    }
  };

  const processFile = async () => {
    if (!selectedFile) {
      addLog('Please select a file first', 'error');
      return;
    }

    if (!password) {
      addLog('Please enter a password', 'error');
      return;
    }

    setIsProcessing(true);
    addLog(`Starting ${operation}ion process...`, 'info');

    try {
      const fileBuffer = await readFileAsArrayBuffer(selectedFile);
      const wordArray = CryptoJS.lib.WordArray.create(fileBuffer);

      let result: CryptoJS.lib.WordArray;

      if (operation === 'encrypt') {
        result = encryptData(wordArray);
        addLog('Encryption completed successfully', 'success');
      } else {
        result = decryptData(wordArray);
        addLog('Decryption completed successfully', 'success');
      }

      // Convert result back to ArrayBuffer
      const resultBuffer = wordArrayToBuffer(result);

      // Create download link
      const blob = new Blob([resultBuffer], { type: 'application/octet-stream' });
      const extension = operation === 'encrypt' ? '.enc' : '';
      saveAs(blob, `${selectedFile.name}${extension}`);

      addLog(`File ${operation}ed and ready for download`, 'success');
    } catch (error) {
      console.error('Error:', error);
      addLog(`Error during ${operation}ion: ${error instanceof Error ? error.message : 'Unknown error'}`, 'error');
    } finally {
      setIsProcessing(false);
    }
  };

  const readFileAsArrayBuffer = (file: File): Promise<ArrayBuffer> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (e.target?.result) {
          resolve(e.target.result as ArrayBuffer);
        } else {
          reject(new Error('Failed to read file'));
        }
      };
      reader.onerror = () => reject(new Error('Error reading file'));
      reader.readAsArrayBuffer(file);
    });
  };

  const getKeyAndIV = () => {
    // Generate key and IV based on password and algorithm
    const keySize = algorithm.includes('256') ? 256 : algorithm.includes('192') ? 192 : 128;
    const key = CryptoJS.PBKDF2(password, 'salt', {
      keySize: keySize / 32,
      iterations: 1000
    });

    // For IV, we'll use a portion of the key
    const iv = CryptoJS.lib.WordArray.create(key.words.slice(0, 4), 16);

    return { key, iv };
  };

  const encryptData = (data: CryptoJS.lib.WordArray): CryptoJS.lib.WordArray => {
    const { key, iv } = getKeyAndIV();

    // Add salt and IV to the beginning of the encrypted data
    const salt = CryptoJS.lib.WordArray.random(128/8);
    const saltAndIv = CryptoJS.enc.Hex.parse(salt.toString() + iv.toString());

    let encrypted;
    if (algorithm.startsWith('AES')) {
      encrypted = CryptoJS.AES.encrypt(data, key, { 
        iv: iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7
      });
    } else if (algorithm === 'DES-CBC') {
      encrypted = CryptoJS.DES.encrypt(data, key, {
        iv: iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7
      });
    } else { // 3DES
      encrypted = CryptoJS.TripleDES.encrypt(data, key, {
        iv: iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7
      });
    }

    // Combine salt, IV and encrypted data
    return CryptoJS.enc.Hex.parse(salt.toString() + iv.toString() + encrypted.ciphertext.toString());
  };

  const decryptData = (data: CryptoJS.lib.WordArray): CryptoJS.lib.WordArray => {
    try {
      // Extract salt, IV and encrypted data
      const salt = CryptoJS.enc.Hex.parse(data.toString().substring(0, 32));
      const iv = CryptoJS.enc.Hex.parse(data.toString().substring(32, 64));
      const encrypted = data.toString().substring(64);

      // Derive key using the same parameters
      const keySize = algorithm.includes('256') ? 256 : algorithm.includes('192') ? 192 : 128;
      const key = CryptoJS.PBKDF2(password, salt, {
        keySize: keySize / 32,
        iterations: 1000
      });

      let decrypted;
      if (algorithm.startsWith('AES')) {
        decrypted = CryptoJS.AES.decrypt(
          { ciphertext: CryptoJS.enc.Hex.parse(encrypted) } as any,
          key,
          { 
            iv: iv,
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
          }
        );
      } else if (algorithm === 'DES-CBC') {
        decrypted = CryptoJS.DES.decrypt(
          { ciphertext: CryptoJS.enc.Hex.parse(encrypted) } as any,
          key,
          { 
            iv: iv,
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
          }
        );
      } else { // 3DES
        decrypted = CryptoJS.TripleDES.decrypt(
          { ciphertext: CryptoJS.enc.Hex.parse(encrypted) } as any,
          key,
          { 
            iv: iv,
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
          }
        );
      }

      return decrypted;
    } catch (error) {
      console.error('Decryption error:', error);
      throw new Error('Failed to decrypt. Please check your password and algorithm selection.');
    }
  };

  const wordArrayToBuffer = (wordArray: CryptoJS.lib.WordArray): ArrayBuffer => {
    const array = new Uint8Array(wordArray.sigBytes);
    for (let i = 0; i < wordArray.sigBytes; i++) {
      array[i] = (wordArray.words[Math.floor(i / 4)] >>> (24 - (i % 4) * 8)) & 0xff;
    }
    return array.buffer;
  };

  const resetForm = () => {
    setSelectedFile(null);
    setPassword('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="tool-container">
      <h2>File Encryption/Decryption Tool</h2>

      <div className="form-group">
        <label>Operation:</label>
        <div className="btn-group">
          <button 
            className={`btn ${operation === 'encrypt' ? 'btn-primary' : 'btn-outline-primary'}`}
            onClick={() => setOperation('encrypt')}
          >
            Encrypt
          </button>
          <button 
            className={`btn ${operation === 'decrypt' ? 'btn-primary' : 'btn-outline-primary'}`}
            onClick={() => setOperation('decrypt')}
          >
            Decrypt
          </button>
        </div>
      </div>

      <div className="form-group">
        <label htmlFor="algorithm">Encryption Algorithm:</label>
        <select 
          id="algorithm" 
          className="form-control"
          value={algorithm}
          onChange={(e) => setAlgorithm(e.target.value as Algorithm)}
        >
          <optgroup label="AES">
            <option value="AES-256-CBC">AES-256-CBC</option>
            <option value="AES-192-CBC">AES-192-CBC</option>
            <option value="AES-128-CBC">AES-128-CBC</option>
          </optgroup>
          <optgroup label="DES">
            <option value="DES-CBC">DES-CBC</option>
            <option value="3DES-CBC">Triple DES-CBC</option>
          </optgroup>
        </select>
      </div>

      <div className="form-group">
        <label htmlFor="file">Select File:</label>
        <input
          id="file"
          type="file"
          className="form-control"
          onChange={handleFileChange}
          ref={fileInputRef}
        />
      </div>

      <div className="form-group">
        <label htmlFor="password">Password:</label>
        <input
          id="password"
          type="password"
          className="form-control"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter encryption/decryption password"
        />
      </div>

      <div className="button-group">
        <button
          className="btn btn-primary"
          onClick={processFile}
          disabled={isProcessing || !selectedFile || !password}
        >
          {isProcessing ? 'Processing...' : operation === 'encrypt' ? 'Encrypt File' : 'Decrypt File'}
        </button>
        <button
          className="btn btn-secondary"
          onClick={resetForm}
          disabled={isProcessing}
        >
          Reset
        </button>
      </div>

      <div className="logs-container">
        <h4>Activity Log:</h4>
        <div className="log-entries">
          {logs.length === 0 ? (
            <div className="log-entry">No activity yet. Select a file and start encrypting/decrypting.</div>
          ) : (
            logs.map((log, index) => (
              <div key={index} className={`log-entry log-${log.type}`}>
                <span className="log-timestamp">[{log.timestamp}] </span>
                <span className="log-message">{log.message}</span>
              </div>
            ))
          )}
        </div>
      </div>

      <style jsx>{`
        .tool-container {
          max-width: 800px;
          margin: 0 auto;
          padding: 20px;
        }
        .form-group {
          margin-bottom: 20px;
        }
        .btn-group {
          display: flex;
          gap: 10px;
          margin: 10px 0;
        }
        .btn {
          padding: 8px 16px;
          border: 1px solid #ccc;
          background: #f8f9fa;
          border-radius: 4px;
          cursor: pointer;
        }
        .btn:disabled {
          opacity: 0.6;
          cursor: not-allowed;
        }
        .btn-primary {
          background: #007bff;
          color: white;
          border-color: #007bff;
        }
        .btn-outline-primary {
          background: white;
          color: #007bff;
          border-color: #007bff;
        }
        .btn-secondary {
          background: #6c757d;
          color: white;
          border-color: #6c757d;
        }
        .form-control {
          width: 100%;
          padding: 8px;
          border: 1px solid #ddd;
          border-radius: 4px;
          margin-top: 5px;
        }
        .logs-container {
          margin-top: 30px;
          border: 1px solid #eee;
          border-radius: 4px;
          padding: 15px;
        }
        .log-entries {
          max-height: 200px;
          overflow-y: auto;
          margin-top: 10px;
        }
        .log-entry {
          padding: 8px;
          border-bottom: 1px solid #eee;
          font-family: monospace;
        }
        .log-timestamp {
          color: #666;
          margin-right: 10px;
        }
        .log-info { color: #17a2b8; }
        .log-success { color: #28a745; }
        .log-warning { color: #ffc107; }
        .log-error { color: #dc3545; }
        .button-group {
          display: flex;
          gap: 10px;
          margin: 20px 0;
        }
      `}</style>
    </div>
  );
};

export default FileEncryptor;
