import React, { useState } from 'react';
import api from '../../services/api';

const JohnTheRipper: React.FC = () => {
  const [hash, setHash] = useState('');
  const [wordlist, setWordlist] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);

  const startCrack = async () => {
    if (!hash) return;
    setLoading(true);
    setError(null);
    setResult(null);
    try {
      const res = await api.post('/john/crack', { hash, wordlist });
      setResult(res.data);
    } catch (e: any) {
      setError(e?.response?.data?.error || 'Failed to start cracking job');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 style={{ marginTop: 0 }}>John the Ripper</h2>
      <p style={{ color: '#9ca3af' }}>
        Provide a password hash to start a cracking job (demo endpoint). Use only on hashes you are authorized to test.
      </p>

      <div style={{ display: 'grid', gap: 8, maxWidth: 720 }}>
        <textarea
          value={hash}
          onChange={(e) => setHash(e.target.value)}
          placeholder={`Paste hash here\n(e.g., SHA-256 or other formats depending on your john setup)`}
          rows={4}
          style={{ padding: 8, borderRadius: 6, border: '1px solid #1f2937', background: '#0b1220', color: '#e5e7eb' }}
        />
        <input
          value={wordlist}
          onChange={(e) => setWordlist(e.target.value)}
          placeholder="Optional wordlist path configured on server"
          style={{ padding: 8, borderRadius: 6, border: '1px solid #1f2937', background: '#0b1220', color: '#e5e7eb' }}
        />
        <div>
          <button onClick={startCrack} disabled={loading || !hash} style={{ padding: '8px 12px', borderRadius: 6 }}>
            {loading ? 'Starting...' : 'Start Cracking'}
          </button>
        </div>
      </div>

      {error && <div style={{ color: '#f87171', marginTop: 12 }}>{error}</div>}
      {result && (
        <pre style={{ marginTop: 12, background: '#0b1220', padding: 12, borderRadius: 8, border: '1px solid #1f2937', overflowX: 'auto' }}>
{JSON.stringify(result, null, 2)}
        </pre>
      )}
    </div>
  );
};

export default JohnTheRipper;
