import React, { useState, useRef, ChangeEvent } from 'react';
import { saveAs } from 'file-saver';
import CryptoJS from 'crypto-js';

type Algorithm =
  | 'AES-128-CBC' | 'AES-192-CBC' | 'AES-256-CBC'
  | 'AES-128-ECB' | 'AES-192-ECB' | 'AES-256-ECB'
  | 'DES-CBC' | 'DES-ECB' | '3DES-CBC' | '3DES-ECB';

interface LogEntry {
  timestamp: string;
  message: string;
  type: 'info' | 'success' | 'error' | 'warning';
}

const FileEncryptor: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [password, setPassword] = useState('');
  const [algorithm, setAlgorithm] = useState<Algorithm>('AES-256-CBC');
  const [isProcessing, setIsProcessing] = useState(false);
  const [logs, setLogs] = useState<LogEntry[]>([]);
  const [operation, setOperation] = useState<'encrypt' | 'decrypt'>('encrypt');
  const fileInputRef = useRef<HTMLInputElement>(null);

  const addLog = (message: string, type: LogEntry['type'] = 'info') => {
    const timestamp = new Date().toLocaleTimeString();
    setLogs(prev => [...prev, { timestamp, message, type }]);
  };

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setSelectedFile(e.target.files[0]);
      addLog(`Selected file: ${e.target.files[0].name}`);
    }
  };

  const readFileAsArrayBuffer = (file: File): Promise<Uint8Array> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => {
        if (reader.result instanceof ArrayBuffer) {
          resolve(new Uint8Array(reader.result));
        } else {
          reject(new Error('Failed to read file as ArrayBuffer'));
        }
      };
      reader.onerror = () => reject(new Error('Error reading file'));
      reader.readAsArrayBuffer(file);
    });
  };

  const uint8ArrayToWordArray = (u8Array: Uint8Array): CryptoJS.lib.WordArray => {
    const words = [];
    for (let i = 0; i < u8Array.length; i += 4) {
      words.push(
        ((u8Array[i] || 0) << 24) |
        ((u8Array[i + 1] || 0) << 16) |
        ((u8Array[i + 2] || 0) << 8) |
        ((u8Array[i + 3] || 0))
      );
    }
    return CryptoJS.lib.WordArray.create(words, u8Array.length);
  };

  const wordArrayToUint8Array = (wordArray: CryptoJS.lib.WordArray): Uint8Array => {
    const { words, sigBytes } = wordArray;
    // Create a new ArrayBuffer and Uint8Array to ensure we don't have SharedArrayBuffer issues
    const buffer = new ArrayBuffer(sigBytes);
    const u8 = new Uint8Array(buffer);
    for (let i = 0; i < sigBytes; i++) {
      u8[i] = (words[i >>> 2] >>> (24 - (i % 4) * 8)) & 0xff;
    }
    return u8;
  };

  const getCipherParams = (algorithm: string) => {
    const [cipher, keySize, mode] = algorithm.split('-');
    let cipherMode = CryptoJS.mode.CBC;
    if (mode === 'ECB') cipherMode = CryptoJS.mode.ECB;
    return { cipher, keySize: keySize ? parseInt(keySize, 10) : 192, mode: cipherMode };
  };

  const encryptData = (data: CryptoJS.lib.WordArray): CryptoJS.lib.WordArray => {
    const { cipher, keySize, mode } = getCipherParams(algorithm);
    const salt = CryptoJS.lib.WordArray.random(128 / 8);
    const key = CryptoJS.PBKDF2(password, salt, { keySize: keySize / 32, iterations: 1000 });
    const iv = mode === CryptoJS.mode.ECB ? undefined : CryptoJS.lib.WordArray.random(128 / 8);

    const cipherParams: any = { mode, padding: CryptoJS.pad.Pkcs7 };
    if (iv) cipherParams.iv = iv;

    let encrypted;
    if (cipher === 'AES') encrypted = CryptoJS.AES.encrypt(data, key, cipherParams);
    else if (cipher === 'DES') encrypted = CryptoJS.DES.encrypt(data, key, cipherParams);
    else encrypted = CryptoJS.TripleDES.encrypt(data, key, cipherParams);

    // Format: [salt][iv][ciphertext]
    const outputParts = [salt.toString()];
    if (iv) outputParts.push(iv.toString());
    outputParts.push(encrypted.ciphertext.toString());
    return CryptoJS.enc.Hex.parse(outputParts.join(''));
  };

  const decryptData = (data: CryptoJS.lib.WordArray): CryptoJS.lib.WordArray => {
    const dataHex = data.toString();
    const { cipher, keySize, mode } = getCipherParams(algorithm);
    const salt = CryptoJS.enc.Hex.parse(dataHex.substring(0, 32));
    let iv, encryptedHex;

    if (mode === CryptoJS.mode.ECB) encryptedHex = dataHex.substring(32);
    else {
      iv = CryptoJS.enc.Hex.parse(dataHex.substring(32, 64));
      encryptedHex = dataHex.substring(64);
    }

    const key = CryptoJS.PBKDF2(password, salt, { keySize: keySize / 32, iterations: 1000 });
    const cipherParams: any = { mode, padding: CryptoJS.pad.Pkcs7 };
    if (iv) cipherParams.iv = iv;

    const encryptedData = CryptoJS.lib.CipherParams.create({ ciphertext: CryptoJS.enc.Hex.parse(encryptedHex) });

    let decrypted;
    if (cipher === 'AES') decrypted = CryptoJS.AES.decrypt(encryptedData, key, cipherParams);
    else if (cipher === 'DES') decrypted = CryptoJS.DES.decrypt(encryptedData, key, cipherParams);
    else decrypted = CryptoJS.TripleDES.decrypt(encryptedData, key, cipherParams);

    return decrypted;
  };

  const processFile = async () => {
    if (!selectedFile || !password) {
      addLog('File and password are required', 'error');
      return;
    }
    setIsProcessing(true);
    addLog(`Starting ${operation}ion...`);

    try {
      const fileData = await readFileAsArrayBuffer(selectedFile);
      let resultArray: Uint8Array = new Uint8Array();

      if (operation === 'encrypt') {
        const wordArray = uint8ArrayToWordArray(fileData);
        const encrypted = encryptData(wordArray);
        resultArray = wordArrayToUint8Array(encrypted);
        // Create a new Blob directly from the Uint8Array
        const blob = new Blob([resultArray], { type: 'application/octet-stream' });
        saveAs(blob, `${selectedFile.name}.enc`);
        addLog('File encrypted successfully', 'success');
      } else {
        const wordArray = uint8ArrayToWordArray(fileData);
        const decrypted = decryptData(wordArray);
        resultArray = wordArrayToUint8Array(decrypted);
        // Create a new Blob directly from the Uint8Array
        const blob = new Blob([resultArray], { type: 'application/octet-stream' });
        saveAs(blob, selectedFile.name.replace(/\.enc$/, '.dec'));
        addLog('File decrypted successfully', 'success');
      }
    } catch (error: any) {
      console.error(error);
      addLog(`Error during ${operation}ion: ${error.message}`, 'error');
    } finally {
      setIsProcessing(false);
    }
  };

  const resetForm = () => {
    setSelectedFile(null);
    setPassword('');
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  return (
    <div className="tool-container">
      <h2>File Encryptor</h2>
      <div>
        <label>Operation:</label>
        <button onClick={() => setOperation('encrypt')} disabled={isProcessing}>Encrypt</button>
        <button onClick={() => setOperation('decrypt')} disabled={isProcessing}>Decrypt</button>
      </div>
      <div>
        <label>Algorithm:</label>
        <select value={algorithm} onChange={e => setAlgorithm(e.target.value as Algorithm)}>
          <option value="AES-256-CBC">AES-256-CBC</option>
          <option value="AES-192-CBC">AES-192-CBC</option>
          <option value="AES-128-CBC">AES-128-CBC</option>
          <option value="AES-256-ECB">AES-256-ECB</option>
          <option value="DES-CBC">DES-CBC</option>
          <option value="DES-ECB">DES-ECB</option>
          <option value="3DES-CBC">3DES-CBC</option>
          <option value="3DES-ECB">3DES-ECB</option>
        </select>
      </div>
      <div>
        <label>File:</label>
        <input type="file" ref={fileInputRef} onChange={handleFileChange} disabled={isProcessing} />
      </div>
      <div>
        <label>Password:</label>
        <input type="password" value={password} onChange={e => setPassword(e.target.value)} disabled={isProcessing} />
      </div>
      <button onClick={processFile} disabled={isProcessing || !selectedFile || !password}>
        {operation === 'encrypt' ? 'Encrypt File' : 'Decrypt File'}
      </button>
      <button onClick={resetForm} disabled={isProcessing}>Reset</button>
      <div className="logs">
        {logs.map((log, i) => (
          <div key={i} style={{ color: log.type === 'error' ? 'red' : 'black' }}>
            [{log.timestamp}] {log.message}
          </div>
        ))}
      </div>
    </div>
  );
};

export default FileEncryptor;
