import React, { useState } from 'react';
import api from '../../services/api.ts';

const WiresharkAnalyzer: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);

  const analyze = async () => {
    setLoading(true);
    setError(null);
    setResult(null);
    try {
      // Demo: no file upload yet; backend returns a placeholder response
      const res = await api.post('/wireshark/analyze', {});
      setResult(res.data);
    } catch (e: any) {
      setError(e?.response?.data?.error || 'Analysis failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 style={{ marginTop: 0 }}>Wireshark (PCAP) Analyzer</h2>
      <p style={{ color: '#9ca3af' }}>
        Analyze packet captures. In this demo, we trigger a sample analysis on the server.
      </p>

      <button onClick={analyze} disabled={loading} style={{ padding: '8px 12px', borderRadius: 6 }}>
        {loading ? 'Analyzing...' : 'Run Analysis'}
      </button>

      {error && <div style={{ color: '#f87171', marginTop: 12 }}>{error}</div>}
      {result && (
        <pre style={{ marginTop: 12, background: '#0b1220', padding: 12, borderRadius: 8, border: '1px solid #1f2937', overflowX: 'auto' }}>
{JSON.stringify(result, null, 2)}
        </pre>
      )}
    </div>
  );
};

export default WiresharkAnalyzer;
