import React, { useState, useRef, ChangeEvent, JSX } from 'react';
import { saveAs } from 'file-saver';
import { Buffer } from 'buffer';
// @ts-ignore - We'll handle the type issues with crypto-js
import CryptoJS from 'crypto-js';

type Algorithm = 'AES-128-CBC' | 'AES-192-CBC' | 'AES-256-CBC' | 'DES-CBC' | '3DES-CBC';

interface LogEntry {
  timestamp: string;
  message: string;
  type: 'info' | 'success' | 'error' | 'warning';
}

const FileEncryptor: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [password, setPassword] = useState('');
  const [algorithm, setAlgorithm] = useState<Algorithm>('AES-256-CBC');
  const [isProcessing, setIsProcessing] = useState(false);
  const [logs, setLogs] = useState<LogEntry[]>([]);
  const [operation, setOperation] = useState<'encrypt' | 'decrypt'>('encrypt');
  const fileInputRef = useRef<HTMLInputElement>(null);

  const addLog = (message: string, type: LogEntry['type'] = 'info') => {
    const timestamp = new Date().toLocaleTimeString();
    setLogs(prev => [...prev, { timestamp, message, type }]);
  };

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setSelectedFile(e.target.files[0]);
      addLog(`Selected file: ${e.target.files[0].name}`, 'info');
    }
  };

  const processFile = async () => {
    if (!selectedFile) {
      addLog('Please select a file first', 'error');
      return;
    }

    if (!password) {
      addLog('Please enter a password', 'error');
      return;
    }

    setIsProcessing(true);
    addLog(`Starting ${operation}ion process...`, 'info');

    try {
      const fileBuffer = await readFileAsArrayBuffer(selectedFile);

      if (operation === 'encrypt') {
        // For encryption, convert ArrayBuffer to WordArray and encrypt
        const wordArray = CryptoJS.lib.WordArray.create(fileBuffer as any);
        const result = encryptData(wordArray);

        // Convert result to ArrayBuffer for download
        const resultBuffer = wordArrayToBuffer(result);
        const blob = new Blob([resultBuffer], { type: 'application/octet-stream' });
        saveAs(blob, `${selectedFile.name}.enc`);
        addLog('File encrypted and ready for download', 'success');
      } else {
        // For decryption, handle both WordArray and ArrayBuffer inputs
        let wordArray: CryptoJS.lib.WordArray;

        // Check if the file has a .enc extension
        if (selectedFile.name.endsWith('.enc')) {
          // For encrypted files, read as binary string first
          const fileData = await readFileAsText(selectedFile);
          wordArray = CryptoJS.enc.Hex.parse(fileData);
        } else {
          // For other files, read as ArrayBuffer
          wordArray = CryptoJS.lib.WordArray.create(fileBuffer as any);
        }

        const result = decryptData(wordArray);

        // Convert decrypted WordArray to string to check for errors
        const decryptedString = CryptoJS.enc.Utf8.stringify(result);

        if (!decryptedString && result.sigBytes > 0) {
          // If decryption didn't produce valid UTF-8, treat as binary data
          const resultBuffer = wordArrayToBuffer(result);
          const blob = new Blob([resultBuffer], { type: 'application/octet-stream' });
          const originalName = selectedFile.name.endsWith('.enc') 
            ? selectedFile.name.slice(0, -4) 
            : `${selectedFile.name}.decrypted`;
          saveAs(blob, originalName);
        } else {
          // If we got a valid string, save it as text
          const blob = new Blob([decryptedString], { type: 'text/plain' });
          const originalName = selectedFile.name.endsWith('.enc') 
            ? selectedFile.name.slice(0, -4) + '.txt' 
            : `${selectedFile.name}.decrypted.txt`;
          saveAs(blob, originalName);
        }

        addLog('File decrypted successfully', 'success');
      }
    } catch (error) {
      console.error('Error:', error);
      addLog(`Error during ${operation}ion: ${error instanceof Error ? error.message : 'Unknown error'}`, 'error');
    } finally {
      setIsProcessing(false);
    }
  };

  const readFileAsArrayBuffer = (file: File): Promise<ArrayBuffer> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (e.target?.result) {
          resolve(e.target.result as ArrayBuffer);
        } else {
          reject(new Error('Failed to read file'));
        }
      };
      reader.onerror = () => reject(new Error('Error reading file'));
      reader.readAsArrayBuffer(file);
    });
  };

  const getKeyAndIV = () => {
    // Generate key and IV based on password and algorithm
    const keySize = algorithm.includes('256') ? 256 : algorithm.includes('192') ? 192 : 128;
    const key = CryptoJS.PBKDF2(password, 'salt', {
      keySize: keySize / 32,
      iterations: 1000
    });

    // For IV, we'll use a portion of the key
    const iv = CryptoJS.lib.WordArray.create(key.words.slice(0, 4), 16);

    return { key, iv };
  };

  const encryptData = (data: CryptoJS.lib.WordArray): CryptoJS.lib.WordArray => {
    const salt = CryptoJS.lib.WordArray.random(128/8);
    const keySize = algorithm.includes('256') ? 256 : algorithm.includes('192') ? 192 : 128;
    const key = CryptoJS.PBKDF2(password, salt, {
      keySize: keySize / 32,
      iterations: 1000
    });
    const iv = CryptoJS.lib.WordArray.random(128/8);

    let encrypted;
    if (algorithm.startsWith('AES')) {
      encrypted = CryptoJS.AES.encrypt(
        { data } as any, 
        key, 
        { 
          iv: iv,
          mode: CryptoJS.mode.CBC,
          padding: CryptoJS.pad.Pkcs7
        }
      );
    } else if (algorithm === 'DES-CBC') {
      encrypted = CryptoJS.DES.encrypt(
        { data } as any,
        key,
        {
          iv: iv,
          mode: CryptoJS.mode.CBC,
          padding: CryptoJS.pad.Pkcs7
        }
      );
    } else { // 3DES
      encrypted = CryptoJS.TripleDES.encrypt(
        { data } as any,
        key,
        {
          iv: iv,
          mode: CryptoJS.mode.CBC,
          padding: CryptoJS.pad.Pkcs7
        }
      );
    }

    // Combine salt, IV and encrypted data
    const saltHex = salt.toString();
    const ivHex = iv.toString();
    const encryptedHex = encrypted.ciphertext.toString();

    return CryptoJS.enc.Hex.parse(saltHex + ivHex + encryptedHex);
  };

  const decryptData = (data: CryptoJS.lib.WordArray): CryptoJS.lib.WordArray => {
    try {
      const dataHex = data.toString();

      // Extract salt (first 32 chars), IV (next 32 chars), and encrypted data (the rest)
      const salt = CryptoJS.enc.Hex.parse(dataHex.substring(0, 32));
      const iv = CryptoJS.enc.Hex.parse(dataHex.substring(32, 64));
      const encryptedHex = dataHex.substring(64);

      // Derive key using the same parameters as encryption
      const keySize = algorithm.includes('256') ? 256 : algorithm.includes('192') ? 192 : 128;
      const key = CryptoJS.PBKDF2(password, salt, {
        keySize: keySize / 32,
        iterations: 1000
      });

      // Create cipher params for decryption
      const cipherParams = CryptoJS.lib.CipherParams.create({
        ciphertext: CryptoJS.enc.Hex.parse(encryptedHex)
      });

      let decrypted;
      if (algorithm.startsWith('AES')) {
        decrypted = CryptoJS.AES.decrypt(
          cipherParams,
          key,
          { 
            iv: iv,
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
          }
        );
      } else if (algorithm === 'DES-CBC') {
        decrypted = CryptoJS.DES.decrypt(
          cipherParams,
          key,
          { 
            iv: iv,
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
          }
        );
      } else { // 3DES
        decrypted = CryptoJS.TripleDES.decrypt(
          cipherParams,
          key,
          { 
            iv: iv,
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
          }
        );
      }

      return decrypted;
    } catch (error) {
      console.error('Decryption error:', error);
      throw new Error('Failed to decrypt. Please check your password and algorithm selection.');
    }
  };

  const wordArrayToBuffer = (wordArray: CryptoJS.lib.WordArray): ArrayBuffer => {
    const array = new Uint8Array(wordArray.sigBytes);
    for (let i = 0; i < wordArray.sigBytes; i++) {
      array[i] = (wordArray.words[Math.floor(i / 4)] >>> (24 - (i % 4) * 8)) & 0xff;
    }
    return array.buffer;
  };

