import React, { useState } from 'react';
import api from '../../services/api.ts';

const presets = [
  { label: 'Quick (-T4 -F)', value: '-T4 -F' },
  { label: 'Service Detect (-sV)', value: '-sV' },
  { label: 'Aggressive (-T4 -A -v)', value: '-T4 -A -v' },
  { label: 'Vuln Scripts (--script=vuln)', value: '--script=vuln' },
];

const NmapScanner: React.FC = () => {
  const [target, setTarget] = useState('');
  const [options, setOptions] = useState('-sV');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState<string>('');
  const [error, setError] = useState<string | null>(null);

  const startScan = async () => {
    if (!target) return;
    setLoading(true);
    setError(null);
    setResult('');
    try {
      const res = await api.post('/nmap/scan', { target, options });
      setResult(res.data?.results || '');
    } catch (e: any) {
      setError(e?.response?.data?.error || 'Scan failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 style={{ marginTop: 0 }}>Nmap</h2>
      <p style={{ color: '#9ca3af' }}>Scan targets for open ports and services.</p>

      <div style={{ display: 'grid', gap: 8, maxWidth: 720 }}>
        <input
          value={target}
          onChange={(e) => setTarget(e.target.value)}
          placeholder="Target (e.g., 192.168.1.1 or example.com)"
          style={{ padding: 8, borderRadius: 6, border: '1px solid #1f2937', background: '#0b1220', color: '#e5e7eb' }}
        />

        <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
          {presets.map(p => (
            <button
              key={p.value}
              onClick={() => setOptions(p.value)}
              style={{ padding: '6px 10px', borderRadius: 6, border: '1px solid #1f2937', background: options === p.value ? '#1f2937' : '#0b1220', color: '#e5e7eb' }}
            >
              {p.label}
            </button>
          ))}
        </div>

        <div style={{ display: 'flex', gap: 8 }}>
          <input
            value={options}
            onChange={(e) => setOptions(e.target.value)}
            placeholder="Custom options"
            style={{ flex: 1, padding: 8, borderRadius: 6, border: '1px solid #1f2937', background: '#0b1220', color: '#e5e7eb' }}
          />
          <button onClick={startScan} disabled={loading || !target} style={{ padding: '8px 12px', borderRadius: 6 }}>
            {loading ? 'Scanning...' : 'Start Scan'}
          </button>
        </div>
      </div>

      {error && <div style={{ color: '#f87171', marginTop: 12 }}>{error}</div>}
      {result && (
        <pre style={{ marginTop: 12, background: '#0b1220', padding: 12, borderRadius: 8, border: '1px solid #1f2937', overflowX: 'auto' }}>
{result}
        </pre>
      )}
    </div>
  );
};

export default NmapScanner;
