import React, { useRef, useState } from 'react';
import api from '../../services/api.ts';

const WiresharkAnalyzer: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);
  const [file, setFile] = useState<File | null>(null);
  const inputRef = useRef<HTMLInputElement | null>(null);

  const onSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const f = e.target.files?.[0] || null;
    setFile(f);
    setResult(null);
    setError(null);
  };

  const analyze = async () => {
    if (!file) return;
    setLoading(true);
    setError(null);
    setResult(null);
    try {
      const form = new FormData();
      form.append('pcap', file);
      const res = await api.post('/wireshark/upload', form, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setResult(res.data);
    } catch (e: any) {
      setError(e?.response?.data?.error || 'Analysis failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 style={{ marginTop: 0 }}>Wireshark (PCAP) Analyzer</h2>
      <p style={{ color: '#9ca3af' }}>
        Upload a .pcap file to compute basic stats (protocol counts). Requires tshark installed on the server.
      </p>

      <div style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
        <input
          type="file"
          accept=".pcap,.pcapng"
          onChange={onSelect}
          ref={inputRef}
          style={{ color: '#e5e7eb' }}
        />
        <button onClick={analyze} disabled={loading || !file} style={{ padding: '8px 12px', borderRadius: 6 }}>
          {loading ? 'Analyzing...' : 'Analyze'}
        </button>
      </div>

      {file && (
        <div style={{ marginTop: 8, color: '#9ca3af' }}>{file.name} ({Math.round(file.size / 1024)} KB)</div>
      )}

      {error && <div style={{ color: '#f87171', marginTop: 12 }}>{error}</div>}
      {result && (
        <div style={{ marginTop: 12 }}>
          <div style={{ background: '#111827', padding: 12, borderRadius: 8, border: '1px solid #1f2937' }}>
            <div><strong>Total Packets:</strong> {result.totalPackets}</div>
            <div style={{ marginTop: 8 }}>
              <strong>Protocols</strong>
              <div style={{ display: 'grid', gap: 6, marginTop: 6 }}>
                {Object.entries(result.protocols || {}).map(([proto, count]) => (
                  <div key={proto} style={{ display: 'flex', justifyContent: 'space-between', color: '#e5e7eb' }}>
                    <span>{proto}</span>
                    <span>{count as any}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WiresharkAnalyzer;
